import React, { useEffect, useRef, useState } from 'react';
import './SessionTimer.css';

const SessionTimer = ({ timeLeft, totalTime, isRunning }) => {
    const radius = 70;
    const circumference = 2 * Math.PI * radius;
    const dashOffset = useRef(circumference);
    const startTimeRef = useRef(null);
    const [elapsedTime, setElapsedTime] = useState(0);
    const [isPaused, setIsPaused] = useState(false); // New state variable

    useEffect(() => {
        let intervalId;

        if (isRunning && !isPaused) {
            if (!startTimeRef.current) {
                startTimeRef.current = Date.now() - elapsedTime * 1000;
            }

            intervalId = setInterval(() => {
                const currentTime = Date.now();
                const elapsedSeconds = Math.floor((currentTime - startTimeRef.current) / 1000);
                dashOffset.current = dashOffset.current - (circumference / totalTime);

                if (elapsedSeconds <= totalTime) {
                    setElapsedTime(elapsedSeconds);
                }
            }, 1000);
        } else {
            dashOffset.current = circumference;
            startTimeRef.current = null;
            setIsPaused(false); // Reset isPaused when not running
        }

        return () => clearInterval(intervalId);
    }, [isRunning, totalTime, timeLeft, circumference, elapsedTime, isPaused]);



    return (
        <svg width="150" height="150" viewBox="0 0 150 150" className="session-timer">
            <circle cx="75" cy="75" r={radius} fill="transparent" stroke="#007bff" strokeWidth="15" />
            <circle
                cx="75"
                cy="75"
                r={radius}
                fill="transparent"
                stroke="#28a745"
                strokeWidth="15"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset.current}
                className="session-timer-progress"
                style={{ animationPlayState: isRunning ? 'running' : 'paused', animationDuration: `${totalTime - elapsedTime}s` }}
            />
            <text x="75" y="85" dominantBaseline="middle" textAnchor="middle" fontSize="25" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:{(timeLeft % 60).toString().padStart(2, '0')}
            </text>

        </svg>
    );
};

export default SessionTimer;
