// SessionTimer.js

// Import necessary React hooks and styles
import React, { useEffect, useRef, useState } from 'react';
import './SessionTimer.css';

// Functional component SessionTimer taking props as arguments
const SessionTimer = ({ timeLeft, totalTime, isRunning }) => {
    // Constants for the radius and circumference of the timer circle
    const radius = 70;
    const circumference = 2 * Math.PI * radius;

    // useRef for maintaining the current dash offset value
    const dashOffset = useRef(circumference);

    // useRef for maintaining the start time of the timer
    const startTimeRef = useRef(null);

    // State variable for maintaining the elapsed time
    const [elapsedTime, setElapsedTime] = useState(0);

    // useEffect hook to manage the timer logic
    useEffect(() => {
        let intervalId;

        // Check if the timer is running
        if (isRunning) {
            console.log("isRunning:", isRunning);
            // If the start time is not set, set it to the current time minus elapsed time
            if (!startTimeRef.current) {
                console.log("startTimeRef: ", startTimeRef.current);
                startTimeRef.current = Date.now() - elapsedTime * 1000;
                console.log("startTimeRef: ", startTimeRef.current);
            }

            // Set up an interval to update the timer every second
            intervalId = setInterval(() => {
                const currentTime = Date.now();
                const elapsedSeconds = Math.floor((currentTime - startTimeRef.current) / 1000);

                // Update the dash offset based on the elapsed time and total time
                dashOffset.current = dashOffset.current - (circumference / totalTime);

                // Update elapsed time when the animation is in progress
                if (elapsedSeconds <= totalTime) {
                    setElapsedTime(elapsedSeconds);
                }
            }, 1000);
        } else {
            // Reset dashOffset and start time when the timer is not running
            dashOffset.current = circumference;
            startTimeRef.current = null;
        }

        // Cleanup function to clear the interval when the component unmounts or dependencies change
        return () => clearInterval(intervalId);
    }, [isRunning, totalTime, circumference, elapsedTime]);

    // Return JSX for rendering the timer as an SVG
    return (
        <svg width="150" height="150" viewBox="0 0 150 150" className="session-timer">
            {/* Outer circle representing the entire timer */}
            <circle cx="75" cy="75" r={radius} fill="transparent" stroke="#007bff" strokeWidth="15" />

            {/* Inner circle representing the progress of the timer */}
            <circle
                cx="75"
                cy="75"
                r={radius}
                fill="transparent"
                stroke="#28a745"
                strokeWidth="15"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset.current}
                className="session-timer-progress"
                style={{ animationPlayState: isRunning ? 'running' : 'paused', animationDuration: `${totalTime - elapsedTime}s` }}
            />

            {/* Text displaying the time remaining in the center of the timer */}
            <text x="75" y="85" dominantBaseline="middle" textAnchor="middle" fontSize="25" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:
                {(timeLeft % 60).toString().padStart(2, '0')}
            </text>
        </svg>
    );
};

// Export the SessionTimer component
export default SessionTimer;
