// SessionTimer.js

import React, { useEffect, useRef } from 'react';
import './SessionTimer.css'; // Import the CSS file for SessionTimer styles

const SessionTimer = ({ timeLeft, totalTime, isRunning }) => {
    const radius = 70; // Increased radius for a larger circle
    const circumference = 2 * Math.PI * radius;
    const dashOffset = useRef(circumference);

    useEffect(() => {
        let intervalId;

        if (isRunning) {
            intervalId = setInterval(() => {
                dashOffset.current = Math.max(0, dashOffset.current - (circumference / totalTime));
            }, 1000);
        } else {
            // Pause the timer
            clearInterval(intervalId);
        }

        return () => clearInterval(intervalId);
    }, [isRunning, totalTime, circumference]);

    useEffect(() => {
        // Reset the dash offset when the timer is reset
        dashOffset.current = circumference;
    }, [timeLeft]);

    return (
        <svg width="150" height="150" viewBox="0 0 150 150" className="session-timer">
            <circle cx="75" cy="75" r={radius} fill="transparent" stroke="#007bff" strokeWidth="15" />
            <circle
                cx="75"
                cy="75"
                r={radius}
                fill="transparent"
                stroke="#28a745"
                strokeWidth="15"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset.current}
                className="session-timer-progress"
            />
            <text x="75" y="85" dominantBaseline="middle" textAnchor="middle" fontSize="25" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:
                {(timeLeft % 60).toString().padStart(2, '0')}
            </text>
        </svg>
    );
};

export default SessionTimer;
