// SessionTimer.js

import React, { useEffect, useRef } from 'react';
import './SessionTimer.css'; // Import the CSS file for SessionTimer styles

const SessionTimer = ({ timeLeft, totalTime, isRunning }) => {
    const radius = 40;
    const circumference = 2 * Math.PI * radius;
    const dashOffset = useRef(circumference);

    useEffect(() => {
        if (isRunning) {
            dashOffset.current = circumference - (timeLeft / totalTime) * circumference;
        } else {
            dashOffset.current = circumference; // Reset to the full circle if not running
        }
    }, [isRunning, timeLeft, totalTime, circumference]);

    return (
        <svg width="100" height="100" viewBox="0 0 100 100" className="session-timer">
            <circle cx="50" cy="50" r={radius} fill="transparent" stroke="#007bff" strokeWidth="10" />
            <circle
                cx="50"
                cy="50"
                r={radius}
                fill="transparent"
                stroke="#28a745"
                strokeWidth="10"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset.current}
                className="session-timer-progress"
            />
            <text x="50" y="50" dominantBaseline="middle" textAnchor="middle" fontSize="12" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:
                {(timeLeft % 60).toString().padStart(2, '0')}
            </text>
        </svg>
    );
};

export default SessionTimer;
