import React, { useState, useEffect } from 'react';
import Controls from './Controls';

function Timer() {
    const [sessionLength, setSessionLength] = useState(25);
    const [breakLength, setBreakLength] = useState(5);
    const [timerLabel, setTimerLabel] = useState('Session');
    const [timeLeft, setTimeLeft] = useState(sessionLength * 60);
    const [isRunning, setIsRunning] = useState(false);
    useEffect(() => {
        let intervalId;

        if (isRunning) {
            intervalId = setInterval(() => {
                setTimeLeft(prevTime => {
                    if (prevTime === 0) {
                        // Timer reached zero, switch to break or session accordingly
                        setTimerLabel(prevLabel => (prevLabel === 'Session' ? 'Break' : 'Session'));
                        return timerLabel === 'Session' ? breakLength * 60 : sessionLength * 60;
                    } else {
                        return prevTime - 1;
                    }
                });
            }, 1000);
        }

        return () => clearInterval(intervalId);

    }, [isRunning, timeLeft, timerLabel, sessionLength, breakLength]);

    const toggleTimer = () => {
        setIsRunning(!isRunning);
    };

    const resetTimer = () => {
        setIsRunning(false);
        setTimerLabel('Session');
        setSessionLength(sessionLength);
        setBreakLength(5)
        setTimeLeft(sessionLength * 60);
    };

    return (
        <div>
            {/* Display components (SessionLength, BreakLength, TimerLabel, TimeLeft, Beep) */}
            <div>
                <p id="timer-label">{timerLabel}</p>
                <p id="time-left">{`${Math.floor(timeLeft / 60)
                    .toString()
                    .padStart(2, '0')}:${(timeLeft % 60).toString().padStart(2, '0')}`}</p>
            </div>
            <Controls toggleTimer={toggleTimer} resetTimer={resetTimer} />
        </div>
    );
}

export default Timer;
