import React, { useState, useEffect } from 'react';
import Controls from './Controls';
import SessionLength from './SessionLength';
import BreakLength from './BreakLength';
import SessionTimer from './SessionTimer';
import './Timer.css';

function Timer() {
    const [isRunning, setIsRunning] = useState(false);
    const [timeLeft, setTimeLeft] = useState(25 * 60);
    const [sessionLength, setSessionLength] = useState(25);
    const [breakLength, setBreakLength] = useState(5);
    const [isPaused, setIsPaused] = useState(false); // New state variable

    useEffect(() => {
        let intervalId;

        if (isRunning && !isPaused) {
            intervalId = setInterval(() => {
                setTimeLeft(prevTime => (prevTime === 0 ? breakLength * 60 : prevTime - 1));
            }, 1000);
        }

        return () => clearInterval(intervalId);
    }, [isRunning, isPaused, timeLeft, breakLength]);

    const toggleTimer = () => {
        setIsPaused(prevIsPaused => !prevIsPaused);
        setIsRunning(prevIsRunning => !prevIsRunning);
    };

    const resetTimer = () => {
        setIsRunning(false);
        setIsPaused(false); // Reset isPaused on timer reset
        setTimeLeft(25 * 60);
    };

    const incrementSession = () => {
        setSessionLength(prevSessionLength => Math.min(prevSessionLength + 1, 60));
    };

    const decrementSession = () => {
        setSessionLength(prevSessionLength => Math.max(prevSessionLength - 1, 1));
    };

    const incrementBreak = () => {
        setBreakLength(prevBreakLength => Math.min(prevBreakLength + 1, 60));
    };

    const decrementBreak = () => {
        setBreakLength(prevBreakLength => Math.max(prevBreakLength - 1, 1));
    };

    return (
        <div className="container mt-5">
            <div className="TimerLabel">
                <div className="col-md-6">
                    <p> Pomodoro Timer</p>
                    <SessionLength
                        sessionLength={sessionLength}
                        incrementSession={incrementSession}
                        decrementSession={decrementSession}
                    />
                    <BreakLength
                        breakLength={breakLength}
                        incrementBreak={incrementBreak}
                        decrementBreak={decrementBreak}
                    />
                </div>
                <div className="row justify-content-center">
                    <SessionTimer timeLeft={timeLeft} totalTime={sessionLength * 60} isRunning={isRunning} />

                    <div className="text-center mt-4">
                        <p id="timer-label" className="h4">
                            Session
                        </p>
                        <p id="time-left" className="h1">
                            {`${Math.floor(timeLeft / 60).toString().padStart(2, '0')}:${(timeLeft % 60)
                                .toString()
                                .padStart(2, '0')}`}
                        </p>
                    </div>
                    <Controls toggleTimer={toggleTimer} resetTimer={resetTimer} />
                </div>
            </div>
        </div>
    );
}

export default Timer;
