// SessionTimer.js

import React from 'react';

const SessionTimer = ({ timeLeft, totalTime }) => {
    const radius = 40;
    const circumference = 2 * Math.PI * radius;
    const dashOffset = circumference - (timeLeft / totalTime) * circumference;

    return (
        <svg width="100" height="100" viewBox="0 0 100 100" className="session-timer">
            <circle
                cx="50"
                cy="50"
                r={radius}
                fill="transparent"
                stroke="#007bff" // Color for time passed
                strokeWidth="10"
            />
            <circle
                cx="50"
                cy="50"
                r={radius}
                fill="transparent"
                stroke="#28a745" // Color for time left
                strokeWidth="10"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset}
            />
            <text x="50" y="50" dominantBaseline="middle" textAnchor="middle" fontSize="12" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:
                {(timeLeft % 60).toString().padStart(2, '0')}
            </text>
        </svg>
    );
};

export default SessionTimer;
