// SessionTimer.js

import React, { useEffect, useRef } from 'react';
import './SessionTimer.css'; // Import the CSS file for SessionTimer styles

const SessionTimer = ({ timeLeft, totalTime, isRunning }) => {
    const radius = 50; // Adjusted radius for a larger circle
    const circumference = 2 * Math.PI * radius;
    const dashOffset = useRef(circumference);

    useEffect(() => {
        if (isRunning) {
            const intervalId = setInterval(() => {
                dashOffset.current = dashOffset.current - (circumference / totalTime);
            }, 1000);

            return () => clearInterval(intervalId);
        }
    }, [isRunning, totalTime, circumference]);

    return (
        <svg width="120" height="120" viewBox="0 0 120 120" className="session-timer">
            <circle cx="60" cy="60" r={radius} fill="transparent" stroke="#007bff" strokeWidth="12" />
            <circle
                cx="60"
                cy="60"
                r={radius}
                fill="transparent"
                stroke="#28a745"
                strokeWidth="12"
                strokeDasharray={circumference}
                strokeDashoffset={dashOffset.current}
                className="session-timer-progress"
            />
            <text x="60" y="70" dominantBaseline="middle" textAnchor="middle" fontSize="20" fill="#333">
                {Math.floor(timeLeft / 60).toString().padStart(2, '0')}:
                {(timeLeft % 60).toString().padStart(2, '0')}
            </text>
        </svg>
    );
};

export default SessionTimer;
